/*
//============================================================================
// Name        : String_diff.cpp
// Description : Given two strings s and t which consist of only lowercase letters.

String t is generated by random shuffling string s and
then add one more letter at a random position.

Find the letter that was added in t.
//============================================================================
*/
#include <iostream>
#include <string>
#include <unordered_map>

using namespace std;

char findTheDifference(string s, string t) {
    unordered_map<char, int> charmap;
    for(unsigned int i=0;i<s.size();i++)
    {
        if(charmap.find(s[i]) != charmap.end())
        {
            charmap[s[i]] += 1;
        }
        else
        {
            charmap[s[i]] = 1;
        }
    }
    for (unsigned int i=0;i<t.size();i++)
    {
    	if(charmap.find(t[i])!=charmap.end())
    	{
			if(charmap.at(t[i])!=0)
			{
				charmap[t[i]] -= 1;
			}
			else
			{
				cout<<t[i];
				return t[i];
			}
    	}
        else
        {
        	cout<<t[i];
            return t[i];
        }
    }
    return NULL;
}


int main() {
	string s = "";
	string t = "";
	string a = "abcd";
	string b = "abcde";
	findTheDifference(s, t);
	findTheDifference(a, b);
	return 0;
}
